"use strict";


$(document).ready(function () {

  fetch('/dashboard/requisicoes_data')
  .then(response => response.json())
  .then(data => {
    // Preparar os dados para o gráfico
    const meses = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sept', 'Oct', 'Nov', 'Dec'];
    const reprovadas = Array(12).fill(0); // Estado 1
    const aprovadas = Array(12).fill(0); // Estado 2

    // Preencher os arrays com os dados recebidos
    data.forEach(item => {
      reprovadas[item.mes - 1] = item.total_reprovadas; // Mês começa em 0 no array
      aprovadas[item.mes - 1] = item.total_aprovadas;
    });

    // Configurar o gráfico
    var echartElemBar = document.getElementById('echartBar');
    if (echartElemBar) {
      var echartBar = echarts.init(echartElemBar);
      echartBar.setOption({
        legend: {
          borderRadius: 0,
          orient: 'horizontal',
          x: 'right',
          data: ['Reprovadas', 'Aprovadas']
        },
        grid: {
          left: '8px',
          right: '8px',
          bottom: '0',
          containLabel: true
        },
        tooltip: {
          show: true,
          backgroundColor: 'rgba(0, 0, 0, .8)'
        },
        xAxis: [{
          type: 'category',
          data: meses,
          axisTick: {
            alignWithLabel: true
          },
          splitLine: {
            show: false
          },
          axisLine: {
            show: true
          }
        }],
        yAxis: [{
          type: 'value',
          axisLabel: {
            formatter: '{value}'
          },
          min: 0,
          interval: 10,
          axisLine: {
            show: false
          },
          splitLine: {
            show: true,
            interval: 'auto'
          }
        }],
        series: [{
          name: 'Reprovadas',
          data: reprovadas,
          type: 'bar',
          color: '#f44336',
          smooth: true,
          itemStyle: {
            emphasis: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowOffsetY: -2,
              shadowColor: 'rgba(0, 0, 0, 0.3)'
            }
          }
        }, {
          name: 'Aprovadas',
          data: aprovadas,
          type: 'bar',
          color: '#20c997',
          smooth: true,
          itemStyle: {
            emphasis: {
              shadowBlur: 10,
              shadowOffsetX: 0,
              shadowOffsetY: -2,
              shadowColor: 'rgba(0, 0, 0, 0.3)'
            }
          }
        }]
      });

      $(window).on('resize', function () {
        setTimeout(function () {
          echartBar.resize();
        }, 500);
      });
    }
  });



  var echartElemPie = document.getElementById('echartPie');

  if (echartElemPie) {
    var echartPie = echarts.init(echartElemPie);
    echartPie.setOption({
      color: ['#62549c', '#7566b5', '#7d6cbb', '#8877bd', '#9181bd', '#6957af'],
      tooltip: {
        show: true,
        backgroundColor: 'rgba(0, 0, 0, .8)'
      },
      series: [{
        name: 'Sales by Country',
        type: 'pie',
        radius: '60%',
        center: ['50%', '50%'],
        data: [{
          value: 535,
          name: 'USA'
        }, {
          value: 310,
          name: 'Brazil'
        }, {
          value: 234,
          name: 'France'
        }, {
          value: 155,
          name: 'BD'
        }, {
          value: 130,
          name: 'UK'
        }, {
          value: 348,
          name: 'India'
        }],
        itemStyle: {
          emphasis: {
            shadowBlur: 10,
            shadowOffsetX: 0,
            shadowColor: 'rgba(0, 0, 0, 0.5)'
          }
        }
      }]
    });
    $(window).on('resize', function () {
      setTimeout(function () {
        echartPie.resize();
      }, 500);
    });
  } // Chart in Dashboard version 1


  var echartElem1 = document.getElementById('echart1');

  if (echartElem1) {
    var echart1 = echarts.init(echartElem1);
    echart1.setOption(_objectSpread({}, echartOptions.lineFullWidth, {}, {
      series: [_objectSpread({
        data: [30, 40, 20, 50, 40, 80, 90]
      }, echartOptions.smoothLine, {
        markArea: {
          label: {
            show: true
          }
        },
        areaStyle: {
          color: 'rgba(102, 51, 153, .2)',
          origin: 'start'
        },
        lineStyle: {
          color: '#663399'
        },
        itemStyle: {
          color: '#663399'
        }
      })]
    }));
    $(window).on('resize', function () {
      setTimeout(function () {
        echart1.resize();
      }, 500);
    });
  } // Chart in Dashboard version 1


  var echartElem2 = document.getElementById('echart2');

  if (echartElem2) {
    var echart2 = echarts.init(echartElem2);
    echart2.setOption(_objectSpread({}, echartOptions.lineFullWidth, {}, {
      series: [_objectSpread({
        data: [30, 10, 40, 10, 40, 20, 90]
      }, echartOptions.smoothLine, {
        markArea: {
          label: {
            show: true
          }
        },
        areaStyle: {
          color: 'rgba(255, 193, 7, 0.2)',
          origin: 'start'
        },
        lineStyle: {
          color: '#FFC107'
        },
        itemStyle: {
          color: '#FFC107'
        }
      })]
    }));
    $(window).on('resize', function () {
      setTimeout(function () {
        echart2.resize();
      }, 500);
    });
  } // Chart in Dashboard version 1


  var echartElem3 = document.getElementById('echart3');

  if (echartElem3) {
    var echart3 = echarts.init(echartElem3);
    echart3.setOption(_objectSpread({}, echartOptions.lineNoAxis, {}, {
      series: [{
        data: [40, 80, 20, 90, 30, 80, 40, 90, 20, 80, 30, 45, 50, 110, 90, 145, 120, 135, 120, 140],
        lineStyle: _objectSpread({
          color: 'rgba(102, 51, 153, 0.8)',
          width: 3
        }, echartOptions.lineShadow),
        label: {
          show: true,
          color: '#212121'
        },
        type: 'line',
        smooth: true,
        itemStyle: {
          borderColor: 'rgba(102, 51, 153, 1)'
        }
      }]
    }));
    $(window).on('resize', function () {
      setTimeout(function () {
        echart3.resize();
      }, 500);
    });
  }
});